import React, { Component } from 'react';
import MyShowList from '../components/MyShowList';
import Table from '@material-ui/core/Table';
import Paper from '@material-ui/core/Paper';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';



class Showing extends Component {
  constructor(props) {
    super(props);
    this.state = {
      myshows: '',
    }
  }

stateRefresh = () => {
  this.setState({
    myshows: '',
  });

this.callApi()
.then(res => this.setState({ myshows: res}))
.catch(err => console.log(err));
}
componentDidMount() {
  this.callApi()
  .then(res => this.setState({ myshows: res}))
  .catch(err => console.log(err));
}

callApi = async () => {
  const response = await fetch('/api/myshows');
  const body = await response.json();
  return body;
}
render() {
  const mapcomponents = (data) =>{
    return data.map((c) => {
      return <MyShowList show_id={c.show_id} ticketing_date={c.ticketing_date} refund_flag={c.refund_flag} />
  })

  }
  const cellList = ["공연", "예매일", "현황"];
  
  return(
    <div>
    <Paper>
        <Table>
          <TableHead>
            <TableRow>
              {cellList.map(c => {
                return <TableCell>{c}</TableCell>
              })}
            </TableRow>

          </TableHead>
          <TableBody>
          {this.state.myshows ? mapcomponents(this.state.myshows) :
          <TableRow>
            <TableCell colSpan="3" align ="center">

            </TableCell>
            </TableRow>}
        </TableBody>
             
        </Table>
 </Paper>
    </div>
  )
}
}

export default Showing;
