import React, { Component } from 'react';
import MyShowList from '../components/MyShowList';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';



class Showing extends Component {
  constructor(props) {
    super(props);
    this.state = {
      myshowss: '',
    }
  }

stateRefresh = () => {
  this.setState({
    myshowss: '',
  });

this.callApi()
.then(res => this.setState({ myshowss: res }))
.catch(err => console.log(err));
}
componentDidMount() {
  this.callApi()
  .then(res => this.setState({ myshowss: res }))
  .catch(err => console.log(err));
}

callApi = async () => {
  const response = await fetch('/api/myshowss');
  const body = await response.json();
  return body
}
render() {

  const cellList = ["공연", "예매일", "현황"];

  return(
    <div>
      <Paper >
        <Table >
          <TableHead>
            <TableRow>
              {cellList.map(c => {
                return <TableCell key={c.toString()}>{c}</TableCell>
              })}
            </TableRow>

          </TableHead>
          <TableBody>
          {this.state.myshowss ? this.state.myshowss.map(c=> {
            return <MyShowList key={c.show_id} show_id={c.show_id} ticketing_date={c.ticketing_date} refund_flag={c.refund_flag} />
          }) : "no data"}

        </TableBody>      
        </Table>
        </Paper>
    </div>
  )
}
}

export default Showing;
